import { Component, DestroyRef, OnInit, inject } from '@angular/core';
import { CurrencyPipe, NgFor, NgIf } from '@angular/common';

interface PromoProduct {
  name: string;
  description: string;
  imageUrl: string;
  originalPrice: number;
  discountPrice: number;
}

@Component({
  selector: 'app-home-departments',
  standalone: true,
  imports: [NgFor, NgIf, CurrencyPipe],
  templateUrl: './home-departments.html',
  styleUrl: './home-departments.scss'
})
export class HomeDepartmentsComponent implements OnInit {
  private readonly destroyRef = inject(DestroyRef);
  private readonly promoDeadline = new Date(Date.now() + 1000 * 60 * 60 * 6);

  timeLeft = this.formatTimeLeft();

  readonly products: PromoProduct[] = [
    {
      name: 'Kit Café Especial Proença',
      description: 'Grãos selecionados com moagem média para coado perfeito.',
      imageUrl: 'https://storage.googleapis.com/sicompra-files/uploads/catalogos/produtos/imagens/jpg/1730233973492.jpg',
      originalPrice: 49.9,
      discountPrice: 24.95
    },
    {
      name: 'Combo Churrasco Completo',
      description: 'Picanha fatiada + linguiça artesanal temperada na casa.',
      imageUrl: 'data:image/jpeg;base64,',
      originalPrice: 129.9,
      discountPrice: 64.95
    },
    {
      name: 'Cesta Fit Hortifrúti',
      description: 'Mix de frutas da estação para sucos e lanches saudáveis.',
      imageUrl: 'data:image/jpeg;base64,',
      originalPrice: 59.9,
      discountPrice: 29.95
    },
    {
      name: 'Seleção de Queijos Especiais',
      description: 'Queijo brie, gouda e gruyère com certificado de origem.',
      imageUrl:'https://www.receiteria.com.br/wp-content/uploads/queijos-especiais-00.png',
      originalPrice: 89.9,
      discountPrice: 44.95
    },
    {
      name: 'Cesta Gourmet Vegan',
      description: 'Produtos veganos selecionados para uma alimentação saudável.',
      imageUrl: 'https://images.tcdn.com.br/img/img_prod/795791/cesta_de_cafe_da_manha_vegana_caminho_da_fazenda_933_1_9740ab85a19a820ab18e7c7e96a49a9d.jpeg',
      originalPrice: 79.9,
      discountPrice: 39.95
    }
  ];

  ngOnInit(): void {
    if (typeof window === 'undefined') {
      return;
    }

    const tick = () => {
      this.timeLeft = this.formatTimeLeft();
    };

    tick();
    const intervalId = window.setInterval(tick, 1000);
    this.destroyRef.onDestroy(() => window.clearInterval(intervalId));
  }

  private formatTimeLeft(): string {
    const diff = this.promoDeadline.getTime() - Date.now();

    if (diff <= 0) {
      return '00:00:00';
    }

    const totalSeconds = Math.floor(diff / 1000);
    const hours = Math.floor(totalSeconds / 3600)
      .toString()
      .padStart(2, '0');
    const minutes = Math.floor((totalSeconds % 3600) / 60)
      .toString()
      .padStart(2, '0');
    const seconds = Math.floor(totalSeconds % 60)
      .toString()
      .padStart(2, '0');

    return `${hours}:${minutes}:${seconds}`;
  }
}
